#include <vector>
#include <string>
using namespace std;
// 注意10，20这种特例
class Solution {
public:
	int numDecodings(string s) {
		vector<int> numbers;
		numbers.resize(s.size());
		numbers.back() = s.back() == '0' ? 0 : 1;
		for (int i = s.size() - 2; i >= 0; --i)
		{
			if (s[i] != '0')
			{
				numbers[i] += numbers[i + 1];
				int sub_number = stoi(s.substr(i, 2));
				if (sub_number <= 26)
					numbers[i] += i + 2 == s.size() ? 1 : numbers[i + 2];
			}
		}
		return numbers[0];
	}
};

int main()
{
	Solution s;
	auto ret = s.numDecodings("");
	return 0;
}